import React, { useState } from 'react';
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from '../UI/Card';
import "./Expenses.css";

const Expenses = (props) => {

    const [enteredYear, setEnteredYear] = useState('2021');

    const saveExpenseYearHandler = (selectedYear) => {
      setEnteredYear(selectedYear);
    };

    const filterByYear = props.item.filter(expense => {
      return expense.date.getFullYear() === parseInt(enteredYear);
    });

    return (
      <Card className="expenses">
        <ExpensesFilter
          selected={enteredYear}
          onSaveExpenseYear={saveExpenseYearHandler}
        />
        {props.item
          // .filter((expense) => 
          //   expense.date.getFullYear() === parseInt(enteredYear))
          .map((filterByYear) => (
            <ExpenseItem
              key={filterByYear.id}
              name={filterByYear.title}
              amount={filterByYear.amount}
              date={filterByYear.date}
            />
        ))}
      </Card>
    );
};

export default Expenses;
