import React, { useState } from 'react';

import './ExpenseForm.css';

const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const [cancelBtn, setCancelBtn] = useState(1);

    const cancelBtnHandler = () => {
        console.log("Cancel button clicked!");
        setCancelBtn(0);
    }

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    };

    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    };
    
    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    };

    const submitHandler = (event) => {
        event.preventDefault();

        // Combine all entered data into the expenseData object
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        };
        // console.log(expenseData);
        
        // Pass on gathered data up to parent component NewExpense where function is defined
        props.onSaveExpenseData(expenseData);
        
        // Clear the input after form submission
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    };

    return (
        <form onSubmit={submitHandler}>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text'
                        // creates a 2-way binding which feeds the state back into the input
                        value={enteredTitle} 
                        onChange={titleChangeHandler} />
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number'
                        value={enteredAmount} 
                        min='0.01' step='0.01' 
                        onChange={amountChangeHandler} />
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' 
                        value={enteredDate}
                        min='2021-01-01' max='2024-12-21' 
                        onChange={dateChangeHandler} />
                </div>
            </div>
            <div className='new-expense__actions'>
                {cancelBtn === 1 ?
                    <>
                        <button onClick={cancelBtnHandler}>Cancel</button>
                        <button type='submit'>Add Expense</button>
                    </>
                    : {addNewBtn}
                }
                
            </div>
        </form>
    )
}

export default ExpenseForm;
