import React, { useState } from 'react';
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from '../UI/Card';
import "./Expenses.css";

const Expenses = (props) => {

  const [enteredYear, setEnteredYear] = useState('2023');

  const saveExpenseYearHandler = (selectedYear) => {
    setEnteredYear(selectedYear);

    const filterByYear = (filteredData) => {
      if (!selectedYear) {
        return filteredData;
      }

      const filteredExpenses = filteredData.filter(
        (expense) => expense.year === selectedYear
      );
      return filteredExpenses;

    }
  };

  useEffect(() => {
    var filteredData = filterByYear(filteredData);
    setEnteredYear(filteredData);
  }, 
  [selectedYear]); 

    

    return (
      <Card className="expenses">
        <ExpensesFilter
          selected={enteredYear}
          onSaveExpenseYear={saveExpenseYearHandler}
        />
        {props.item.map((filteredExpenses) => (
          <ExpenseItem
            key={filteredExpenses.id}
            name={filteredExpenses.title}
            amount={filteredExpenses.amount}
            date={filteredExpenses.date}
          />
        ))}
      </Card>
    );
};

export default Expenses;
