import React, { useState } from 'react';
import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props) => {

    const [addingExpense, setAddingExpense] = useState(1);

    const newExpenseBtnHandler = () => {
        console.log("New Expense button clicked");
        setAddingExpense(0);
    }

    const doneExpenseBtnHandler = () => {
        setAddingExpense(1);
    }
    
    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,  // this is expected to be the object generated in the submit handler in ExpenseForm
            id: Math.random().toString() 
        };
        // console.log(expenseData);

        // Passes on gathered data up to parent component App where function is defined
        props.onAddExpense(expenseData);
        setAddingExpense(1);
    };
    
    return (
        <div className='new-expense'>
            {addingExpense === 1 ? 
                <button onClick={newExpenseBtnHandler}>Add New Expense</button> 
                :
                <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} onCancel={doneExpenseBtnHandler}/>
            }
            
            
        </div>
    )
}

export default NewExpense;
